import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import Carousal from '../components/Carousal';
import Card from '../components/Card';

export default function Home() {
  const [foodCategory, setFoodCategory] = useState([]);
  const [foodItem, setFoodItem] = useState([]);

  const loadData = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/foodData', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const data = await response.json();
      setFoodCategory(data[0]); // assuming response[0] contains food categories
      setFoodItem(data[1]); // assuming response[1] contains food items
    } catch (error) {
      console.error('Error fetching data:', error.message);
    }
  };

  useEffect(() => {
    loadData();
  }, []);

  return (
    <div>
      <div>
        <Navbar />
      </div>
      <div>
        <Carousal />
      </div>
      <div className="container mt-3">
        <div className="row row-cols-1 row-cols-md-2 row-cols-lg-4">
          {foodCategory !== [] 
          ? foodCategory.map((data) => (
              <div key={data._id} className="col mb-4">
                <div className="card">
                  <div className="card-body">
                    <h5 className="card-title fs-3 m-3">{data.CategoryName}</h5>
                    <hr />
                    <div className="row row-cols-1 row-cols-md-2 row-cols-lg-1">
                      {foodItem.length !== 0 ? (
                        foodItem
                          .filter((item) => item.CategoryName === data.CategoryName)
                          .map((filterItems) => (
                            <div key={filterItems._id} className="col mb-4">
                              <Card />
                            </div>
                          ))
                      ) : (
                        <div className="col">
                          <div>No such Data Found</div>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            ))
           : (
            <div className="col">
              <div>No categories found</div>
            </div>
          )}
        </div>
      </div>
      <div>
        <Footer />
      </div>
    </div>
  );
}
