import { useState } from 'react'
import React from 'react'
import {Link} from 'react-router-dom'

export default function SignUp() {
    const[credentials, setcredentials] = useState({name:'', email:'', password:'', geolocation:''})
    const handleSubmit = async(e)=>{
           e.preventDefault();
           const response =fetch("http://localhost:5000/api/createuser",{
            method:'POST',
            header:{
                'Content-Type':'application/json'
            },
            body:JSON.stringify()
           })
    }
    const onChange=()=>{
        setcredentials({...credentials,[event.target.name]:event})
    }
    return (
        <>
        <div className='container'>
        <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label for="name" className="form-label">Name</label>
                    <input type="text" className="form-control" name='name' value={credentials.name} on Change={onChange}/>
                </div>
                <div className="mb-3">
                    <label for="exampleInputEmail1" className="form-label">Email address</label>
                    <input type="email" className="form-control"  name='email' value={credentials.email} on Change={onChange} id="exampleInputEmail1" />
                    <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
                </div>
                <div className="mb-3">
                    <label for="exampleInputPassword1" className="form-label">Password</label>
                    <input type="password" className="form-control" id="exampleInputPassword1"  name='password' value={credentials.password} on Change={onChange} />
                </div>
                <div className="mb-3">
                    <label for="exampleInputPassword1" className="form-label">Address</label>
                    <input type="text" className="form-control" id="exampleInputPassword1"  name='address' value={credentials.geolocation} on Change={onChange} />
                </div>

                <button type="submit" className="m-3 btn btn-primary">Submit</button>
                <Link to="/login" className='m-3 btn btn-danger'>Already a user?</Link>
            </form>
        </div>

            
        </>
    )
}
