import { useState } from 'react'
import React from 'react'
import {Link} from 'react-router-dom'

export default function SignUp() {
    const[credentials, setcredentials] = useState({name:'', email:'', password:'', geolocation:''})
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
          const response = await fetch("http://localhost:5000/api/createuser", {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({name: credentials.name, email: credentials.email, password: credentials.password, geolocation: credentials.geolocation})
          });
      
          if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
          }
      
          const json = await response.json();
          console.log(json);
      
          if (!json.success) {
            alert("Enter valid credentials");
          }
        } catch (error) {
          console.error('Network error:', error);
        }
      };
      
    const onChange=(event)=>{
        setcredentials({...credentials,[event.target.name]:event.target.value})
    }
    return (
        <>
        <div className='container'>
        <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlfor="name" className="form-label">Name</label>
                    <input type="text" className="form-control" name='name' value={credentials.name} onChange={onChange}/>
                </div>
                <div className="mb-3">
                    <label htmlfor="exampleInputEmail1" className="form-label">Email address</label>
                    <input type="email" className="form-control"  name='email' value={credentials.email} onChange={onChange} id="exampleInputEmail1" />
                    <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
                </div>
                <div className="mb-3">
                    <label htmlfor="exampleInputPassword1" className="form-label">Password</label>
                    <input type="password" className="form-control" id="exampleInputPassword1"  name='password' value={credentials.password} onChange={onChange} />
                </div>
                <div className="mb-3">
                    <label htmlfor="exampleInputPassword1" className="form-label">Address</label>
                    <input type="text" className="form-control" id="exampleInputPassword1"  name='geolocation' value={credentials.geolocation} onChange={onChange} />
                </div>

                <button type="submit" className="m-3 btn btn-primary">Submit</button>
                <Link to="/login" className='m-3 btn btn-danger'>Already a user?</Link>
            </form>
        </div>

            
        </>
    )
}
